import React , {useState,useEffect} from 'react'
import './Index.css'

const Index = () => {

    const year = new Date().getFullYear();

      const [word, setQuote] = useState("");

      const getQuotes = () => {
        fetch("https://wordsapiv1.p.mashape.com/words/example")
          .then((res) => res.json())
          .then((data) => {
            let randomNum = Math.floor(Math.random() * data.length);
            setQuote(data[randomNum]);
          });
      };

      useEffect(() => {
        getQuotes();
      }, []);

  return (
    <div className="footer-main">
      <footer>
        <div className="list-items-footer">
          <h2 className="quote-head">Daily words of the Day</h2>
          <p className="quote-text">
            " {quote.text} "
          </p>
          <cite className="quote-author">~ {quote.author}</cite>
          <button
            onClick={getQuotes}
            type="submit"
            className="button-new-quote"
          >
            Get new Word
          </button>
        </div>
        <h4>Copyright &copy; {year} || Doodle.keep.co.in</h4>
      </footer>
    </div>
  );
}

export default Index
