import React , {useState,useEffect} from 'react'
import './Index.css'

const Index = () => {

    const year = new Date().getFullYear();

      const [words, setWord] = useState("");

      const getWord = () => {
        fetch("https://wordsapiv1.p.mashape.com/words/example")
          .then((res) => res.json())
          .then((data) => {
            let randomNum = Math.floor(Math.random() * data.length);
            setWord(data[randomNum]);
          });
      };

      useEffect(() => {
        getWord();
      }, []);

  return (
    <div className="footer-main">
      <footer>
        <div className="list-items-footer">
          <h2 className="quote-head">Daily words of the Day</h2>
          <h3>{words.words}</h3>
          <p className="quote-text">
            <ul>
              <li>Synonyms</li>
              <li>{words.synonyms}</li>
              <li>Antonyms</li>
            </ul>
          </p>
          <cite className="quote-author">~ {words.author}</cite>
          <button
            onClick={getWord}
            type="submit"
            className="button-new-quote"
          >
            Get new Word
          </button>
        </div>
        <h4>Copyright &copy; {year} || Doodle.keep.co.in</h4>
      </footer>
    </div>
  );
}

export default Index
